class BoletoTest extends TestCase
{
    protected static $pagador;

    protected static $beneficiario;

    public static function setUpBeforeClass(): void
    {
        self::$beneficiario = new Pessoa([
            'nome'      => 'ACME',
            'endereco'  => 'Rua um, 123',
            'cep'       => '99999-999',
            'uf'        => 'UF',
            'cidade'    => 'CIDADE',
            'documento' => '99.999.999/9999-99',
        ]);

        self::$pagador = new Pessoa([
            'nome'      => 'Cliente',
            'endereco'  => 'Rua um, 123',
            'bairro'    => 'Bairro',
            'cep'       => '99999-999',
            'uf'        => 'UF',
            'cidade'    => 'CIDADE',
            'documento' => '999.999.999-99',
        ]);
    }

    public static function tearDownAfterClass(): void
    {
        $aFiles = [
            __DIR__,
            'files',
        ];
        $files = glob(implode(DIRECTORY_SEPARATOR, $aFiles) . '/*'); // get all file names
        foreach ($files as $file) {
            if (is_file($file)) {
                @unlink($file);
            }
        }
    }

    public function testAddBoletos()
    {
        $boleto = new Boleto\Banrisul([
            'logo'                   => realpath(__DIR__ . '/../../logos/') . DIRECTORY_SEPARATOR . '041.png',
            'dataVencimento'         => $this->vencimento(),
            'valor'                  => $this->valor(),
            'multa'                  => $this->multa(),
            'juros'                  => $this->juros(),
            'numero'                 => 1,
            'diasBaixaAutomatica'    => 20,
            'numeroDocumento'        => 1,
            'pagador'                => self::$pagador,
            'beneficiario'           => self::$beneficiario,
            'carteira'               => 1,
            'agencia'                => 1111,
            'conta'                  => 22222,
            'descricaoDemonstrativo' => ['demonstrativo 1', 'demonstrativo 2', 'demonstrativo 3'],
            'instrucoes'             => ['instrucao 1', 'instrucao 2', 'instrucao 3'],
            'aceite'                 => $this->aceite(),
            'especieDoc'             => 'DM',
        ]);

        $pdf = new Pdf();
        $pdf->addBoletos([$boleto, $boleto]);

        $this->assertNotNull($pdf->gerarBoleto($pdf::OUTPUT_STRING));
    }

    public function testSave()
    {
        $boleto = new Boleto\Banrisul([
            'logo'                   => realpath(__DIR__ . '/../../logos/') . DIRECTORY_SEPARATOR . '041.png',
            'dataVencimento'         => $this->vencimento(),
            'valor'                  => $this->valor(),
            'multa'                  => $this->multa(),
            'juros'                  => $this->juros(),
            'numero'                 => 1,
            'diasBaixaAutomatica'    => 20,
            'numeroDocumento'        => 1,
            'pagador'                => self::$pagador,
            'beneficiario'           => self::$beneficiario,
            'carteira'               => 1,
            'agencia'                => 1111,
            'conta'                  => 22222,
            'descricaoDemonstrativo' => ['demonstrativo 1', 'demonstrativo 2', 'demonstrativo 3'],
            'instrucoes'             => ['instrucao 1', 'instrucao 2', 'instrucao 3'],
            'aceite'                 => $this->aceite(),
            'especieDoc'             => 'DM',
        ]);

        $file = implode(DIRECTORY_SEPARATOR, [
            __DIR__,
            'files',
            'boletos1.pdf',
        ]);

        $pdf = new Pdf();
        $pdf->addBoletos([$boleto, $boleto]);
        $file2 = $pdf->gerarBoleto($pdf::OUTPUT_SAVE, $file);

        $this->assertEquals($file, $file2);
        $this->assertFileExists($file);
    }

